import SwiftUI
import WebKit

// MARK: - Diagram Renderer View

/// Main view for rendering AI-generated diagrams in chat messages
/// Supports both LaTeX/TikZ and SVG diagram formats
struct DiagramRendererView: View {
    let diagramType: String
    let diagramCode: String
    let diagramTitle: String?
    let renderingHint: NetworkService.DiagramRenderingHint?

    @State private var isLoading = true
    @State private var hasError = false
    @State private var errorMessage = ""
    @State private var renderedImage: UIImage?
    @State private var diagramScale: CGFloat = 1.0

    var body: some View {
        VStack(alignment: .leading, spacing: 12) {
            // Diagram content (title and badge removed for cleaner display)
            Group {
                if hasError {
                    errorView
                } else if isLoading {
                    loadingView
                } else if let image = renderedImage {
                    renderedImageView(image: image)
                } else {
                    fallbackCodeView
                }
            }
            .frame(maxWidth: .infinity)
            .background(Color.gray.opacity(0.05))
            .cornerRadius(12)
            .overlay(
                RoundedRectangle(cornerRadius: 12)
                    .stroke(Color.blue.opacity(0.2), lineWidth: 1)
            )
        }
        .onAppear {
            renderDiagram()
        }
    }

    // MARK: - Loading View

    private var loadingView: some View {
        VStack(spacing: 12) {
            ProgressView()
                .scaleEffect(0.8)

            Text("Rendering \(diagramType.uppercased()) diagram...")
                .font(.system(size: 12))
                .foregroundColor(.secondary)
        }
        .frame(height: 100)
        .frame(maxWidth: .infinity)
    }

    // MARK: - Error View

    private var errorView: some View {
        VStack(spacing: 8) {
            Image(systemName: "info.circle")
                .foregroundColor(.blue)
                .font(.system(size: 20))

            Text("Cannot Generate Diagram")
                .font(.system(size: 14, weight: .medium))
                .foregroundColor(.primary)

            Text(errorMessage)
                .font(.system(size: 12))
                .foregroundColor(.secondary)
                .multilineTextAlignment(.center)
                .padding(.horizontal, 8)
        }
        .padding(16)
        .frame(maxWidth: .infinity)
    }

    // MARK: - Rendered Image View

    private func renderedImageView(image: UIImage) -> some View {
        ZStack {
            Image(uiImage: image)
                .resizable()
                .aspectRatio(contentMode: .fit)
                .scaleEffect(diagramScale)
                .clipped()
        }
        .frame(maxHeight: calculateMaxHeight())
        .background(Color.white)
        .onTapGesture(count: 2) {
            // Double tap to reset zoom
            withAnimation(.spring()) {
                diagramScale = 1.0
            }
        }
        .gesture(
            MagnificationGesture()
                .onChanged { value in
                    diagramScale = max(0.5, min(3.0, value))
                }
        )
    }

    // MARK: - Fallback Code View

    private var fallbackCodeView: some View {
        VStack(alignment: .leading, spacing: 8) {
            HStack {
                Image(systemName: "code")
                    .foregroundColor(.gray)
                Text("Raw \(diagramType.uppercased()) Code")
                    .font(.system(size: 12, weight: .medium))
                    .foregroundColor(.secondary)
                Spacer()
            }

            ScrollView {
                Text(diagramCode)
                    .font(.system(size: 10, design: .monospaced))
                    .foregroundColor(.primary)
                    .frame(maxWidth: .infinity, alignment: .leading)
            }
            .frame(maxHeight: 150)
        }
        .padding(12)
    }

    // MARK: - Diagram Rendering Logic

    private func renderDiagram() {
        Task {
            await MainActor.run {
                isLoading = true
                hasError = false
                errorMessage = ""
            }

            do {
                let image = try await renderDiagramImage()

                await MainActor.run {
                    renderedImage = image
                    isLoading = false
                }
            } catch {
                print("‚ö†Ô∏è [DiagramRenderer] Primary render failed for \(diagramType): \(error.localizedDescription)")
                // Fallback: render a simple SVG placeholder so the chat never shows a hard error state
                do {
                    let fallback = try await renderFallbackSVG()
                    await MainActor.run {
                        renderedImage = fallback
                        isLoading = false
                    }
                } catch {
                    await MainActor.run {
                        hasError = true
                        errorMessage = error.localizedDescription
                        isLoading = false
                    }
                }
            }
        }
    }

    private func renderDiagramImage() async throws -> UIImage {

        switch diagramType.lowercased() {
        case "matplotlib":
            print("üé® [DiagramImage] ‚û°Ô∏è Using Matplotlib renderer (base64 PNG)")
            return try MatplotlibRenderer.shared.renderMatplotlib(diagramCode)
        case "png":
            print("üé® [DiagramImage] ‚û°Ô∏è Using PNG renderer (base64 PNG from Graphviz)")
            return try MatplotlibRenderer.shared.renderPNG(diagramCode)
        case "latex", "tikz":
            print("üé® [DiagramImage] ‚û°Ô∏è Using LaTeX renderer")
            return try await LaTeXRenderer.shared.renderLaTeX(diagramCode, hint: renderingHint)
        case "svg":
            print("üé® [DiagramImage] ‚û°Ô∏è Using SVG renderer")
            return try await SVGRenderer.shared.renderSVG(diagramCode, hint: renderingHint)
        default:
            print("üé® [DiagramImage] ‚ùå Unsupported format: \(diagramType)")
            throw DiagramError.unsupportedFormat(diagramType)
        }
    }

    private func renderFallbackSVG() async throws -> UIImage {
        let title = (diagramTitle ?? "Diagram")
            .replacingOccurrences(of: "&", with: "&amp;")
            .replacingOccurrences(of: "<", with: "&lt;")
            .replacingOccurrences(of: ">", with: "&gt;")
            .replacingOccurrences(of: "\"", with: "&quot;")
        let displayTitle = title.count > 45 ? String(title.prefix(42)) + "..." : title
        let typeLabel = diagramType.uppercased()

        let fallbackSVG = """
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 400 180">
            <rect width="400" height="180" fill="#f0f4ff" rx="12" stroke="#b3c5f7" stroke-width="1.5"/>
            <text x="200" y="72" text-anchor="middle" font-family="Arial, sans-serif" font-size="15" font-weight="bold" fill="#2c3e7a">\(displayTitle)</text>
            <text x="200" y="102" text-anchor="middle" font-family="Arial, sans-serif" font-size="12" fill="#5a6899">\(typeLabel) Diagram</text>
            <text x="200" y="132" text-anchor="middle" font-family="Arial, sans-serif" font-size="10" fill="#8895b3">Simplified view</text>
        </svg>
        """
        print("üé® [DiagramRenderer] Rendering fallback SVG for \(diagramType)")
        return try await SVGRenderer.shared.renderSVG(
            fallbackSVG,
            hint: NetworkService.DiagramRenderingHint(width: 400, height: 180, background: "white", scaleFactor: 1.0)
        )
    }

    private func calculateMaxHeight() -> CGFloat {
        if let hint = renderingHint {
            return CGFloat(hint.height)
        }
        return 300 // Default max height
    }
}

// MARK: - Matplotlib Renderer

/// Handles Matplotlib PNG diagram rendering (base64 decode only - no WebView needed!)
class MatplotlibRenderer {
    static let shared = MatplotlibRenderer()

    private init() {}

    func renderMatplotlib(_ base64PngCode: String) throws -> UIImage {
        print("üé® [MatplotlibRenderer] Starting base64 PNG decode...")
        print("üé® [MatplotlibRenderer] Code length: \(base64PngCode.count) characters")

        // Decode base64 string to Data
        guard let imageData = Data(base64Encoded: base64PngCode, options: .ignoreUnknownCharacters) else {
            print("üé® [MatplotlibRenderer] ‚ùå Failed to decode base64 string")
            throw DiagramError.invalidCode("Invalid base64 PNG data from matplotlib")
        }

        print("üé® [MatplotlibRenderer] ‚úÖ Decoded \(imageData.count) bytes")

        // Create UIImage from data
        guard let image = UIImage(data: imageData) else {
            print("üé® [MatplotlibRenderer] ‚ùå Failed to create UIImage from data")
            throw DiagramError.renderingFailed("Could not create image from matplotlib PNG data")
        }

        print("üé® [MatplotlibRenderer] ‚úÖ Created UIImage successfully")
        print("üé® [MatplotlibRenderer] Image size: \(image.size.width)x\(image.size.height)")
        print("üé® [MatplotlibRenderer] Image scale: \(image.scale)")

        return image
    }

    func renderPNG(_ dataURL: String) throws -> UIImage {
        print("üé® [MatplotlibRenderer] Rendering PNG from data URL...")
        print("üé® [MatplotlibRenderer] Data URL length: \(dataURL.count) characters")

        // Extract base64 data from data URL format: "data:image/png;base64,{base64_string}"
        let base64String: String
        if dataURL.hasPrefix("data:image/png;base64,") {
            base64String = String(dataURL.dropFirst("data:image/png;base64,".count))
            print("üé® [MatplotlibRenderer] Extracted base64 from data URL")
        } else {
            // Assume it's already base64 without the data URL prefix
            base64String = dataURL
            print("üé® [MatplotlibRenderer] Using string as-is (no data URL prefix)")
        }

        // Decode base64 string to Data
        guard let imageData = Data(base64Encoded: base64String, options: .ignoreUnknownCharacters) else {
            print("üé® [MatplotlibRenderer] ‚ùå Failed to decode base64 string")
            throw DiagramError.invalidCode("Invalid base64 PNG data from Graphviz")
        }

        print("üé® [MatplotlibRenderer] ‚úÖ Decoded \(imageData.count) bytes")

        // Create UIImage from data
        guard let image = UIImage(data: imageData) else {
            print("üé® [MatplotlibRenderer] ‚ùå Failed to create UIImage from data")
            throw DiagramError.renderingFailed("Could not create image from PNG data")
        }

        print("üé® [MatplotlibRenderer] ‚úÖ Created UIImage successfully")
        print("üé® [MatplotlibRenderer] Image size: \(image.size.width)x\(image.size.height)")
        print("üé® [MatplotlibRenderer] Image scale: \(image.scale)")

        return image
    }
}

// MARK: - LaTeX Renderer

/// Handles LaTeX/TikZ diagram rendering using MathJax or similar web-based rendering
class LaTeXRenderer {
    static let shared = LaTeXRenderer()

    // ‚úÖ FIX: Retain active renderers until they complete
    private var activeRenderers: [String: LaTeXWebRenderer] = [:]
    private let queue = DispatchQueue(label: "latex.renderer.manager")

    private init() {}

    func renderLaTeX(_ code: String, hint: NetworkService.DiagramRenderingHint?) async throws -> UIImage {
        let continuationId = "LaTeX-\(UUID().uuidString.prefix(8))"

        print("üé® [LaTeXRenderer] Starting LaTeX rendering...")
        print("üé® [LaTeXRenderer] Code length: \(code.count) characters")
        print("üé® [LaTeXRenderer] Continuation ID: \(continuationId)")
        if let hint = hint {
            print("üé® [LaTeXRenderer] Size: \(hint.width)x\(hint.height)")
        }

        // For LaTeX rendering, we'll use a WebView with MathJax.
        // Wrap with a hard timeout so the continuation can't hang forever if the WebView stalls.
        return try await withThrowingTaskGroup(of: UIImage.self) { group in
            group.addTask { [self] in
                return try await withCheckedThrowingContinuation { continuation in
                    DispatchQueue.main.async {
                        print("üé® [LaTeXRenderer] Creating WebView renderer...")

                        let renderer = LaTeXWebRenderer(
                            latexCode: code,
                            hint: hint,
                            completion: { [weak self] result in
                                print("üé® [LaTeXRenderer] ‚úÖ Renderer completion called for \(continuationId)")
                                continuation.resume(with: result)

                                // ‚úÖ FIX: Remove renderer from active list after completion
                                self?.queue.async {
                                    self?.activeRenderers.removeValue(forKey: continuationId)
                                    print("üé® [LaTeXRenderer] ‚úÖ Removed renderer \(continuationId) from active list (count: \(self?.activeRenderers.count ?? 0))")
                                }
                            }
                        )

                        // ‚úÖ FIX: Store renderer in active list to prevent deallocation
                        self.queue.async {
                            self.activeRenderers[continuationId] = renderer
                            print("üé® [LaTeXRenderer] ‚úÖ Stored renderer \(continuationId) in active list (count: \(self.activeRenderers.count))")
                        }

                        print("üé® [LaTeXRenderer] WebView renderer created and started")
                    }
                }
            }
            group.addTask {
                try await Task.sleep(nanoseconds: 10_000_000_000) // 10-second hard timeout
                throw DiagramError.renderingFailed("LaTeX rendering timeout (10s)")
            }
            let image = try await group.next()!
            group.cancelAll()
            return image
        }
    }
}

// MARK: - Debug Logging Utility

class DiagramDebugLogger {
    static let shared = DiagramDebugLogger()
    private var activeContinuations: Set<String> = []
    private let queue = DispatchQueue(label: "diagram.debug.logger")

    private init() {}

    func logContinuationCreated(_ id: String) {
        queue.async {
            self.activeContinuations.insert(id)
            print("üîç [DEBUG] Continuation CREATED: \(id)")
            print("üîç [DEBUG] Active continuations: \(self.activeContinuations.count)")
        }
    }

    func logContinuationResumed(_ id: String) {
        queue.async {
            self.activeContinuations.remove(id)
            print("üîç [DEBUG] Continuation RESUMED: \(id)")
            print("üîç [DEBUG] Active continuations: \(self.activeContinuations.count)")
        }
    }

    func logContinuationLeak(_ id: String) {
        queue.async {
            print("üîç [DEBUG] ‚ùå Continuation LEAKED: \(id)")
            print("üîç [DEBUG] All active: \(self.activeContinuations)")
        }
    }

    func getActiveContinuations() -> Int {
        return queue.sync { activeContinuations.count }
    }
}

// MARK: - SVG Renderer

/// Handles SVG diagram rendering using native iOS SVG capabilities
class SVGRenderer {
    static let shared = SVGRenderer()

    // ‚úÖ FIX: Retain active renderers until they complete
    private var activeRenderers: [String: SVGImageRenderer] = [:]
    private let queue = DispatchQueue(label: "svg.renderer.manager")

    private init() {}

    func renderSVG(_ svgCode: String, hint: NetworkService.DiagramRenderingHint?) async throws -> UIImage {
        let continuationId = "SVG-\(UUID().uuidString.prefix(8))"
        DiagramDebugLogger.shared.logContinuationCreated(continuationId)

        print("üé® [SVGRenderer] Starting SVG rendering...")
        print("üé® [SVGRenderer] Code length: \(svgCode.count) characters")
        print("üé® [SVGRenderer] Continuation ID: \(continuationId)")
        if let hint = hint {
            print("üé® [SVGRenderer] Size: \(hint.width)x\(hint.height)")
        }

        return try await withCheckedThrowingContinuation { continuation in
            print("üîç [DEBUG] withCheckedThrowingContinuation ENTERED for \(continuationId)")

            DispatchQueue.main.async {
                print("üé® [SVGRenderer] Creating SVG renderer...")
                print("üîç [DEBUG] About to create SVGImageRenderer with continuation \(continuationId)")

                let renderer = SVGImageRenderer(
                    svgCode: svgCode,
                    hint: hint,
                    continuationId: continuationId,  // Pass the ID for tracking
                    completion: { [weak self] result in
                        print("üîç [DEBUG] SVGImageRenderer completion called for \(continuationId)")
                        DiagramDebugLogger.shared.logContinuationResumed(continuationId)
                        continuation.resume(with: result)
                        print("üîç [DEBUG] continuation.resume(with:) called for \(continuationId)")

                        // ‚úÖ FIX: Remove renderer from active list after completion
                        self?.queue.async {
                            self?.activeRenderers.removeValue(forKey: continuationId)
                            print("üé® [SVGRenderer] ‚úÖ Removed renderer \(continuationId) from active list (count: \(self?.activeRenderers.count ?? 0))")
                        }
                    }
                )

                // ‚úÖ FIX: Store renderer in active list to prevent deallocation
                self.queue.async {
                    self.activeRenderers[continuationId] = renderer
                    print("üé® [SVGRenderer] ‚úÖ Stored renderer \(continuationId) in active list (count: \(self.activeRenderers.count))")
                }

                print("üé® [SVGRenderer] Starting render process...")
                renderer.render()
            }
        }
    }
}

// Weak proxy prevents WKWebView from retaining LaTeXWebRenderer strongly via the message handler
private class WeakScriptMessageHandler: NSObject, WKScriptMessageHandler {
    weak var delegate: WKScriptMessageHandler?
    init(_ delegate: WKScriptMessageHandler) { self.delegate = delegate }
    func userContentController(_ controller: WKUserContentController, didReceive message: WKScriptMessage) {
        delegate?.userContentController(controller, didReceive: message)
    }
}

// MARK: - LaTeX Web Renderer

/// WebKit-based LaTeX renderer using MathJax
class LaTeXWebRenderer: NSObject, WKNavigationDelegate {
    private let webView: WKWebView
    private let completion: (Result<UIImage, Error>) -> Void
    private let latexCode: String
    private let hint: NetworkService.DiagramRenderingHint?

    // ‚úÖ FIX: Flag to prevent double completion call
    private var hasCompleted = false
    private let completionLock = NSLock()

    init(latexCode: String, hint: NetworkService.DiagramRenderingHint?, completion: @escaping (Result<UIImage, Error>) -> Void) {
        self.latexCode = latexCode
        self.hint = hint
        self.completion = completion

        let config = WKWebViewConfiguration()
        self.webView = WKWebView(frame: .zero, configuration: config)

        super.init()

        // Register renderComplete handler before loading HTML so local-bundle callbacks aren't missed
        config.userContentController.add(WeakScriptMessageHandler(self), name: "renderComplete")
        webView.navigationDelegate = self
        setupWebView()
    }

    private func setupWebView() {
        let width = hint?.width ?? 400
        let height = hint?.height ?? 300

        print("üé® [LaTeXWebRenderer] Setting up WebView: \(width)x\(height)")
        webView.frame = CGRect(x: 0, y: 0, width: width, height: height)

        let htmlContent = generateHTMLForLaTeX()
        print("üé® [LaTeXWebRenderer] HTML content length: \(htmlContent.count) characters")
        print("üé® [LaTeXWebRenderer] Loading HTML content...")
        webView.loadHTMLString(htmlContent, baseURL: nil)
    }

    private func generateHTMLForLaTeX() -> String {
        let backgroundColor = hint?.background ?? "white"

        // Use local bundle to avoid CDN latency and work offline
        let mathJaxScriptTag: String
        if let bundleURL = Bundle.main.url(forResource: "mathjax.min", withExtension: "js") {
            mathJaxScriptTag = "<script src=\"\(bundleURL.absoluteString)\"></script>"
        } else {
            mathJaxScriptTag = "<script id=\"MathJax-script\" async src=\"https://cdn.jsdelivr.net/npm/mathjax@3.2.2/es5/tex-svg.js\"></script>"
        }

        return """
        <!DOCTYPE html>
        <html>
        <head>
            <meta charset="utf-8">
            <meta name="viewport" content="width=device-width, initial-scale=1">
            <script>
                window.MathJax = {
                    tex: {
                        inlineMath: [['$', '$'], ['\\\\(', '\\\\)']],
                        displayMath: [['$$', '$$'], ['\\\\[', '\\\\]']],
                        packages: ['base', 'ams', 'noerrors', 'noundefined', 'bbox']
                    },
                    svg: {
                        fontCache: 'global'
                    },
                    startup: {
                        ready: function () {
                            MathJax.startup.defaultReady();
                            MathJax.startup.promise.then(function () {
                                window.webkit.messageHandlers.renderComplete.postMessage('ready');
                            });
                        }
                    }
                };
            </script>
            \(mathJaxScriptTag)
            <style>
                body {
                    margin: 0;
                    padding: 20px;
                    background-color: \(backgroundColor);
                    font-family: 'Computer Modern', serif;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    min-height: calc(100vh - 40px);
                }
                .diagram-container {
                    text-align: center;
                    max-width: 100%;
                }
            </style>
        </head>
        <body>
            <div class="diagram-container">
                $$\(latexCode)$$
            </div>
        </body>
        </html>
        """
    }

    func webView(_ webView: WKWebView, didFinish navigation: WKNavigation!) {
        // renderComplete message handler already registered in init.
        // Safety-net: if MathJax silently fails to fire, capture after 3s.
        DispatchQueue.main.asyncAfter(deadline: .now() + 3.0) { [weak self] in
            guard let self, !self.hasCompleted else { return }
            self.captureWebViewImage()
        }
    }

    func webView(_ webView: WKWebView, didFail navigation: WKNavigation!, withError error: Error) {
        print("üé® [LaTeXWebRenderer] ‚ùå Navigation failed: \(error.localizedDescription)")
        completeOnce(with: .failure(error))
    }

    // ‚úÖ FIX: Thread-safe completion wrapper to prevent double resume
    private func completeOnce(with result: Result<UIImage, Error>) {
        completionLock.lock()
        defer { completionLock.unlock() }

        guard !hasCompleted else {
            print("‚ö†Ô∏è [LaTeXWebRenderer] Completion already called - ignoring duplicate call")
            return
        }

        hasCompleted = true
        print("üé® [LaTeXWebRenderer] Calling completion (first time)")
        completion(result)
    }

    private func captureWebViewImage() {
        print("üé® [LaTeXWebRenderer] === STARTING WEBVIEW IMAGE CAPTURE ===")
        print("üé® [LaTeXWebRenderer] Taking WebView snapshot...")
        print("üé® [LaTeXWebRenderer] WebView frame: \(webView.frame)")
        print("üé® [LaTeXWebRenderer] WebView content size: \(webView.scrollView.contentSize)")

        webView.takeSnapshot(with: nil) { [weak self] image, error in
            print("üé® [LaTeXWebRenderer] === SNAPSHOT RESULT RECEIVED ===")

            if let image = image {
                print("üé® [LaTeXWebRenderer] ‚úÖ Snapshot captured successfully")
                print("üé® [LaTeXWebRenderer] - Image size: \(image.size)")
                print("üé® [LaTeXWebRenderer] - Image scale: \(image.scale)")
                print("üé® [LaTeXWebRenderer] - Total pixels: \(Int(image.size.width * image.size.height))")
                print("üé® [LaTeXWebRenderer] Calling completeOnce(.success)")
                self?.completeOnce(with: .success(image))
            } else if let error = error {
                print("üé® [LaTeXWebRenderer] ‚ùå Snapshot failed with error")
                print("üé® [LaTeXWebRenderer] - Error type: \(type(of: error))")
                print("üé® [LaTeXWebRenderer] - Error description: \(error.localizedDescription)")
                print("üé® [LaTeXWebRenderer] Calling completeOnce(.failure) with error")
                self?.completeOnce(with: .failure(error))
            } else {
                print("üé® [LaTeXWebRenderer] ‚ùå Snapshot failed with no error information")
                print("üé® [LaTeXWebRenderer] This is an unexpected state - no image and no error")
                print("üé® [LaTeXWebRenderer] Calling completeOnce(.failure) with generic error")
                self?.completeOnce(with: .failure(DiagramError.renderingFailed("Unknown error during LaTeX rendering")))
            }

            print("üé® [LaTeXWebRenderer] === LATEX RENDERING COMPLETE ===")
        }

        print("üé® [LaTeXWebRenderer] Snapshot capture request submitted to WebView")
    }
}

// MARK: - WKScriptMessageHandler Extension

extension LaTeXWebRenderer: WKScriptMessageHandler {
    func userContentController(_ userContentController: WKUserContentController, didReceive message: WKScriptMessage) {
        print("üé® [LaTeXWebRenderer] Received message: '\(message.name)' = '\(message.body)'")

        if message.name == "renderComplete" {
            // MathJax has finished typesetting ‚Äî capture immediately
            captureWebViewImage()
        }
    }
}

// MARK: - SVG Image Renderer

/// Native iOS SVG renderer
class SVGImageRenderer: NSObject {
    private let svgCode: String
    private let hint: NetworkService.DiagramRenderingHint?
    private let completion: (Result<UIImage, Error>) -> Void
    private let continuationId: String
    private var webView: WKWebView?
    private var timeoutTimer: Timer?
    private var hasCompleted = false

    init(svgCode: String, hint: NetworkService.DiagramRenderingHint?, continuationId: String, completion: @escaping (Result<UIImage, Error>) -> Void) {
        self.svgCode = svgCode
        self.hint = hint
        self.completion = completion
        self.continuationId = continuationId
        super.init()
    }

    deinit {
        if !hasCompleted {
            DiagramDebugLogger.shared.logContinuationLeak(continuationId)
        }
    }

    func render() {
        guard !hasCompleted else {
            return
        }

        DispatchQueue.main.async {
            self.renderSVGWithWebView()
        }
    }

    private func renderSVGWithWebView() {
        let width = hint?.width ?? 400
        let height = hint?.height ?? 300

        let config = WKWebViewConfiguration()
        config.websiteDataStore = WKWebsiteDataStore.nonPersistent()
        config.suppressesIncrementalRendering = false
        config.allowsInlineMediaPlayback = true

        webView = WKWebView(frame: CGRect(x: 0, y: 0, width: width, height: height), configuration: config)
        webView?.navigationDelegate = self

        let htmlContent = """
        <!DOCTYPE html>
        <html>
        <head>
            <meta charset="utf-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <style>
                body {
                    margin: 0;
                    padding: 20px;
                    background-color: \(hint?.background ?? "white");
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    min-height: calc(100vh - 40px);
                    font-family: Arial, sans-serif;
                }
                svg {
                    max-width: 100%;
                    height: auto;
                }
                .svg-container {
                    text-align: center;
                    width: 100%;
                    height: 100%;
                }
                .debug-info {
                    position: fixed;
                    top: 5px;
                    right: 5px;
                    font-size: 8px;
                    background: rgba(0,0,0,0.1);
                    padding: 2px;
                }
            </style>
            <script>
                console.log('üé® [WebView] HTML document loaded');
                window.addEventListener('load', function() {
                    console.log('üé® [WebView] Window load event fired');
                    setTimeout(function() {
                        console.log('üé® [WebView] SVG should be rendered by now');
                    }, 100);
                });

                window.addEventListener('DOMContentLoaded', function() {
                    console.log('üé® [WebView] DOMContentLoaded fired');
                });
            </script>
        </head>
        <body>
            <div class="svg-container">
                \(svgCode)
            </div>
            <div class="debug-info">SVG-\(Int.random(in: 1000...9999))</div>
        </body>
        </html>
        """

        // Validate SVG
        let svgLower = svgCode.lowercased()
        if !svgLower.contains("<svg") {
            print("‚ùå [SVGImageRenderer] Invalid SVG: missing <svg> tag")
            completeWithResult(image: nil, error: DiagramError.invalidCode("SVG content missing <svg> tag"))
            return
        }

        // Set up timeout timer
        timeoutTimer = Timer.scheduledTimer(withTimeInterval: 3.0, repeats: false) { [weak self] _ in
            self?.handleTimeout()
        }

        guard let webView = webView else {
            print("‚ùå [SVGImageRenderer] WebView creation failed")
            completeWithResult(image: nil, error: DiagramError.renderingFailed("WebView creation failed"))
            return
        }

        webView.loadHTMLString(htmlContent, baseURL: nil)
    }

    private func captureSnapshot() {
        guard let webView = webView, !hasCompleted else {
            return
        }

        webView.takeSnapshot(with: nil) { [weak self] image, error in
            if let error = error {
                print("‚ùå [SVGImageRenderer] Snapshot failed: \(error.localizedDescription)")
            }
            self?.completeWithResult(image: image, error: error)
        }
    }

    private func completeWithResult(image: UIImage?, error: Error?) {
        guard !hasCompleted else {
            return
        }

        hasCompleted = true

        if let error = error {
            print("‚ùå [SVGRenderer] Rendering failed: \(error.localizedDescription)")
        }

        // Clean up timeout timer
        if let timer = timeoutTimer {
            timer.invalidate()
            timeoutTimer = nil
        }

        // Clear webView to prevent memory leaks
        if let webView = webView {
            webView.navigationDelegate = nil
            self.webView = nil
        }

        // Call completion with appropriate result
        if let image = image {
            completion(.success(image))
        } else if let error = error {
            completion(.failure(error))
        } else {
            completion(.failure(DiagramError.renderingFailed("Unknown error during SVG rendering")))
        }
    }

    private func handleTimeout() {
        guard !hasCompleted else {
            return
        }

        // Try alternative rendering approach as fallback
        attemptAlternativeRendering()
    }

    // NEW: Alternative rendering fallback
    private func attemptAlternativeRendering() {
        print("üé® [SVGRenderer] === ATTEMPTING ALTERNATIVE RENDERING ===")
        print("üé® [SVGRenderer] WebView-based rendering failed, trying alternative approach")

        guard !hasCompleted else {
            print("üé® [SVGRenderer] ‚ùå Already completed, skipping alternative rendering")
            return
        }

        // Try to create a simple bitmap representation of basic SVG
        if let simpleImage = createBasicSVGFallbackImage() {
            print("üé® [SVGRenderer] ‚úÖ Created fallback bitmap image")
            print("üé® [SVGRenderer] Fallback image size: \(simpleImage.size)")
            completeWithResult(image: simpleImage, error: nil)
        } else {
            print("üé® [SVGRenderer] ‚ùå Alternative rendering also failed")
            completeWithResult(image: nil, error: DiagramError.renderingFailed("SVG rendering timeout - all methods failed"))
        }
    }

    // NEW: Create a basic fallback image for simple SVG content
    private func createBasicSVGFallbackImage() -> UIImage? {
        print("üé® [SVGRenderer] === CREATING BASIC SVG FALLBACK IMAGE ===")

        let width = hint?.width ?? 400
        let height = hint?.height ?? 300
        let size = CGSize(width: CGFloat(width), height: CGFloat(height))

        print("üé® [SVGRenderer] Creating fallback with size: \(size)")

        // Analyze SVG for basic shapes we can render
        let svgLower = svgCode.lowercased()

        let renderer = UIGraphicsImageRenderer(size: size)
        let image = renderer.image { context in
            let cgContext = context.cgContext

            // Set background color
            let backgroundColor = parseBackgroundColor()
            cgContext.setFillColor(backgroundColor)
            cgContext.fill(CGRect(origin: .zero, size: size))

            // Try to extract and render basic shapes
            if svgLower.contains("circle") {
                drawCircleFromSVG(in: cgContext, size: size)
            } else if svgLower.contains("rect") {
                drawRectFromSVG(in: cgContext, size: size)
            } else if svgLower.contains("line") {
                drawLineFromSVG(in: cgContext, size: size)
            } else {
                // Fallback: draw a placeholder
                drawPlaceholder(in: cgContext, size: size)
            }
        }

        print("üé® [SVGRenderer] ‚úÖ Fallback image created successfully")
        return image
    }

    private func parseBackgroundColor() -> CGColor {
        if let bg = hint?.background {
            switch bg.lowercased() {
            case "white": return UIColor.white.cgColor
            case "black": return UIColor.black.cgColor
            case "gray", "grey": return UIColor.gray.cgColor
            case "blue": return UIColor.blue.cgColor
            default: return UIColor.white.cgColor
            }
        }
        return UIColor.white.cgColor
    }

    private func drawCircleFromSVG(in context: CGContext, size: CGSize) {
        print("üé® [SVGRenderer] Drawing basic circle fallback")

        context.setFillColor(UIColor.blue.cgColor)
        context.setStrokeColor(UIColor.systemBlue.cgColor)
        context.setLineWidth(2.0)

        let radius = min(size.width, size.height) * 0.3
        let center = CGPoint(x: size.width / 2, y: size.height / 2)
        let rect = CGRect(x: center.x - radius, y: center.y - radius, width: radius * 2, height: radius * 2)

        context.fillEllipse(in: rect)
        context.strokeEllipse(in: rect)
    }

    private func drawRectFromSVG(in context: CGContext, size: CGSize) {
        print("üé® [SVGRenderer] Drawing basic rectangle fallback")

        context.setFillColor(UIColor.green.cgColor)
        context.setStrokeColor(UIColor.systemGreen.cgColor)
        context.setLineWidth(2.0)

        let rectSize = CGSize(width: size.width * 0.6, height: size.height * 0.4)
        let rect = CGRect(x: (size.width - rectSize.width) / 2,
                         y: (size.height - rectSize.height) / 2,
                         width: rectSize.width,
                         height: rectSize.height)

        context.fill(rect)
        context.stroke(rect)
    }

    private func drawLineFromSVG(in context: CGContext, size: CGSize) {
        print("üé® [SVGRenderer] Drawing basic line fallback")

        context.setStrokeColor(UIColor.red.cgColor)
        context.setLineWidth(3.0)

        context.move(to: CGPoint(x: size.width * 0.2, y: size.height * 0.5))
        context.addLine(to: CGPoint(x: size.width * 0.8, y: size.height * 0.5))
        context.strokePath()
    }

    private func drawPlaceholder(in context: CGContext, size: CGSize) {
        print("üé® [SVGRenderer] Drawing generic SVG placeholder")

        // Draw a border
        context.setStrokeColor(UIColor.lightGray.cgColor)
        context.setLineWidth(1.0)
        context.stroke(CGRect(origin: .zero, size: size).insetBy(dx: 10, dy: 10))

        // Draw "SVG" text
        let font = UIFont.systemFont(ofSize: 24, weight: .medium)
        let attrs: [NSAttributedString.Key: Any] = [
            .font: font,
            .foregroundColor: UIColor.gray
        ]

        let text = "SVG Diagram"
        let textSize = text.size(withAttributes: attrs)
        let textRect = CGRect(x: (size.width - textSize.width) / 2,
                             y: (size.height - textSize.height) / 2,
                             width: textSize.width,
                             height: textSize.height)

        text.draw(in: textRect, withAttributes: attrs)
    }
}

// MARK: - WKNavigationDelegate

extension SVGImageRenderer: WKNavigationDelegate {
    func webView(_ webView: WKWebView, didStartProvisionalNavigation navigation: WKNavigation!) {
        print("üé® [SVGRenderer] === NAVIGATION: DID START PROVISIONAL ===")
        print("üé® [SVGRenderer] Navigation object: \(navigation?.description ?? "nil")")
        print("üé® [SVGRenderer] WebView URL: \(webView.url?.absoluteString ?? "No URL")")
        print("üé® [SVGRenderer] WebView loading: \(webView.isLoading)")
        print("üé® [SVGRenderer] Expected next: didFinish or didFailProvisionalNavigation")
    }

    func webView(_ webView: WKWebView, didCommit navigation: WKNavigation!) {
        print("üé® [SVGRenderer] === NAVIGATION: DID COMMIT ===")
        print("üé® [SVGRenderer] Navigation committed successfully")
        print("üé® [SVGRenderer] Navigation object: \(navigation?.description ?? "nil")")
        print("üé® [SVGRenderer] WebView URL: \(webView.url?.absoluteString ?? "No URL")")
        print("üé® [SVGRenderer] Expected next: didFinish")
    }

    func webView(_ webView: WKWebView, didFinish navigation: WKNavigation!) {
        print("üé® [SVGRenderer] === NAVIGATION: DID FINISH (SUCCESS) ===")
        print("üé® [SVGRenderer] üéâ WebView finished loading HTML content")
        print("üé® [SVGRenderer] Navigation object: \(navigation?.description ?? "nil")")
        print("üé® [SVGRenderer] WebView title: '\(webView.title ?? "No title")'")
        print("üé® [SVGRenderer] WebView URL: \(webView.url?.absoluteString ?? "No URL")")
        print("üé® [SVGRenderer] WebView loading: \(webView.isLoading)")
        print("üé® [SVGRenderer] WebView content size: \(webView.scrollView.contentSize)")
        print("üé® [SVGRenderer] Has completed: \(hasCompleted)")

        // Check if already completed
        if hasCompleted {
            print("üé® [SVGRenderer] ‚ö†Ô∏è Already completed, skipping snapshot")
            return
        }

        // Wait a bit for SVG to render, then capture
        print("üé® [SVGRenderer] Scheduling snapshot capture in 0.5 seconds...")
        DispatchQueue.main.asyncAfter(deadline: .now() + 0.5) {
            print("üé® [SVGRenderer] ‚è∞ 0.5 second delay elapsed, starting snapshot capture")
            self.captureSnapshot()
        }
    }

    func webView(_ webView: WKWebView, didFail navigation: WKNavigation!, withError error: Error) {
        print("üé® [SVGRenderer] === NAVIGATION: DID FAIL (ERROR) ===")
        print("üé® [SVGRenderer] ‚ùå WebView navigation failed")
        print("üé® [SVGRenderer] Navigation object: \(navigation?.description ?? "nil")")
        print("üé® [SVGRenderer] Error type: \(type(of: error))")
        print("üé® [SVGRenderer] Error code: \((error as NSError).code)")
        print("üé® [SVGRenderer] Error domain: \((error as NSError).domain)")
        print("üé® [SVGRenderer] Error description: \(error.localizedDescription)")
        print("üé® [SVGRenderer] Error user info: \((error as NSError).userInfo)")

        print("üé® [SVGRenderer] Calling completion with navigation error...")
        completeWithResult(image: nil, error: error)
    }

    func webView(_ webView: WKWebView, didFailProvisionalNavigation navigation: WKNavigation!, withError error: Error) {
        print("üé® [SVGRenderer] === NAVIGATION: DID FAIL PROVISIONAL (ERROR) ===")
        print("üé® [SVGRenderer] ‚ùå WebView provisional navigation failed")
        print("üé® [SVGRenderer] This typically occurs during initial load setup")
        print("üé® [SVGRenderer] Navigation object: \(navigation?.description ?? "nil")")
        print("üé® [SVGRenderer] Error type: \(type(of: error))")
        print("üé® [SVGRenderer] Error code: \((error as NSError).code)")
        print("üé® [SVGRenderer] Error domain: \((error as NSError).domain)")
        print("üé® [SVGRenderer] Error description: \(error.localizedDescription)")
        print("üé® [SVGRenderer] Error user info: \((error as NSError).userInfo)")

        print("üé® [SVGRenderer] Calling completion with provisional navigation error...")
        completeWithResult(image: nil, error: error)
    }

    // NEW: Add decidePolicyFor methods to catch navigation policy issues
    func webView(_ webView: WKWebView, decidePolicyFor navigationAction: WKNavigationAction, decisionHandler: @escaping (WKNavigationActionPolicy) -> Void) {
        print("üé® [SVGRenderer] === NAVIGATION POLICY: ACTION ===")
        print("üé® [SVGRenderer] Navigation type: \(navigationAction.navigationType.rawValue)")
        print("üé® [SVGRenderer] Request URL: \(navigationAction.request.url?.absoluteString ?? "nil")")
        print("üé® [SVGRenderer] Source frame: \(navigationAction.sourceFrame.request.url?.absoluteString ?? "nil")")
        print("üé® [SVGRenderer] Target frame: \(navigationAction.targetFrame?.request.url?.absoluteString ?? "nil")")
        print("üé® [SVGRenderer] Allowing navigation...")
        decisionHandler(.allow)
    }

    func webView(_ webView: WKWebView, decidePolicyFor navigationResponse: WKNavigationResponse, decisionHandler: @escaping (WKNavigationResponsePolicy) -> Void) {
        print("üé® [SVGRenderer] === NAVIGATION POLICY: RESPONSE ===")
        print("üé® [SVGRenderer] Response URL: \(navigationResponse.response.url?.absoluteString ?? "nil")")
        print("üé® [SVGRenderer] MIME type: \(navigationResponse.response.mimeType ?? "nil")")

        if let httpResponse = navigationResponse.response as? HTTPURLResponse {
            print("üé® [SVGRenderer] HTTP status code: \(httpResponse.statusCode)")
            print("üé® [SVGRenderer] HTTP headers: \(httpResponse.allHeaderFields)")
        }

        print("üé® [SVGRenderer] Can show MIME type: \(navigationResponse.canShowMIMEType)")
        print("üé® [SVGRenderer] Allowing response...")
        decisionHandler(.allow)
    }

    // NEW: Add WebView content process methods
    func webViewWebContentProcessDidTerminate(_ webView: WKWebView) {
        print("üé® [SVGRenderer] === CRITICAL: WEB CONTENT PROCESS TERMINATED ===")
        print("üé® [SVGRenderer] ‚ùå WebView content process was terminated")
        print("üé® [SVGRenderer] This means the WebView renderer crashed")
        print("üé® [SVGRenderer] Calling completion with process termination error...")
        completeWithResult(image: nil, error: DiagramError.renderingFailed("WebView content process terminated"))
    }
}

// MARK: - Diagram Errors

enum DiagramError: LocalizedError {
    case unsupportedFormat(String)
    case renderingFailed(String)
    case invalidCode(String)

    var errorDescription: String? {
        switch self {
        case .unsupportedFormat(let format):
            return "Unsupported diagram format: \(format)"
        case .renderingFailed(let reason):
            return "Diagram rendering failed: \(reason)"
        case .invalidCode(let reason):
            return "Invalid diagram code: \(reason)"
        }
    }
}

